import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
//import { AuthService } from 'src/app/services/authentication/auth-service.service';
import { AuthService } from '@auth0/auth0-angular';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent implements OnInit {

  loggedIn = false;
  formLogin: FormGroup;
  formSingup: FormGroup;

  constructor(private formBuilder: FormBuilder, private router: Router, public auth: AuthService) {
    this.formLogin = this.formBuilder.group({
      nickname: ['',Validators.required],
      password: ['',Validators.required]
    })
    this.formSingup = this.formBuilder.group({
      nicknameRegister: ['',Validators.required],
      passwordRegister: ['',Validators.required],
      emailRegister: ['', Validators.required]
    })
  }

  ngOnInit(){
  }
  login() {
    this.auth.loginWithRedirect();
    const val = this.formLogin.value;
    const user = {nickname: val.nickname, password: val.password};
    // this.authService.login(user).subscribe( data => {
    //   console.log('data de login', data);
    // });
  }
  register() {
    const val = this.formSingup.value;
    const user = { nickname: val.nicknameRegister, password: val.passwordRegister, email: val.emailRegister };
    // this.authService.register(user).subscribe(data => {
    //   console.log('data de register', data);
    // });
  }

