  singup(){
    const valSingup = this.formSingup.value;
    console.log('val', valSingup)
    // if (val.email && val.password && val.nickname) {
    //     this.authService.singup(val.email, val.password)
    //         .subscribe(
    //             () => {
    //                 console.log("User is logged in");
    //                 this.router.navigateByUrl('/');
    //             }
    //         );
    // }
  }
}
