import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
//import { AuthService } from 'src/app/services/authentication/auth-service.service';
import { AuthService } from '@auth0/auth0-angular';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent implements OnInit {

  loggedIn = false;
  formLogin: FormGroup;
  formSingup: FormGroup;

  constructor(private formBuilder: FormBuilder, private router: Router, public auth: AuthService) {
    this.formLogin = this.formBuilder.group({
      nickname: ['',Validators.required],
      password: ['',Validators.required]
    })
    this.formSingup = this.formBuilder.group({
      nicknameRegister: ['',Validators.required],
      passwordRegister: ['',Validators.required],
      emailRegister: ['', Validators.required]
    })
  }

  ngOnInit(){
  }
  login() {
    this.auth.loginWithRedirect();
    const val = this.formLogin.value;
    const user = {nickname: val.nickname, password: val.password};
    // this.authService.login(user).subscribe( data => {
    //   console.log('data de login', data);
    // });
  }
  register() {
    const val = this.formSingup.value;
    const user = { nickname: val.nicknameRegister, password: val.passwordRegister, email: val.emailRegister };
    // this.authService.register(user).subscribe(data => {
    //   console.log('data de register', data);
    // });
  }

  // login() {
  //   const val = this.formLogin.value;
  //   console.log('val', val)
  //   if (val.nickname && val.password) {
  //       this.authService.login(val)
  //   }
  // }
  singup(){
    const valSingup = this.formSingup.value;
    console.log('val', valSingup)
    // if (val.email && val.password && val.nickname) {
    //     this.authService.singup(val.email, val.password)
    //         .subscribe(
    //             () => {
    //                 console.log("User is logged in");
    //                 this.router.navigateByUrl('/');
    //             }
    //         );
    // }
  }
}
<section class="login-main height-screen">

    <div class="login-main__lefth">
      <div class="login-main__childrenL">
        <h1 class="title-login">¡Hola!</h1>
        <p><strong class="subtitle-login">Bienvenido al juego PIEDRA PAPEL O TIJERA,</strong><br>aquí podrás divertirte con tus amigos<br>o retarte a ti mismo!.</p>

        <form [formGroup]="formLogin">
            <div class="mb-3">
              <label for="exampleInputEmail1" class="form-label">Nickname</label>
              <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" formControlName="nickname">
            </div>
            <div class="mb-3">
              <label for="exampleInputPassword1" class="form-label">Password</label>
              <input type="password" class="form-control" id="exampleInputPassword1" formControlName="password">
            </div>
            <button type="button" class="btn btn-dark" (click)="login()">Ingresar</button>
            <br>
            <br>
            <div id="emailHelp" class="form-text">Si no tienes usuario registrate!</div>
            <button type="button" class="btn btn-dark" data-bs-toggle="modal" data-bs-target="#exampleModal">
                Registrarse
            </button>

              <!-- Modal -->
              <div class="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div class="modal-dialog">
                  <div class="modal-content">
                    <div class="modal-header">-
                      <h1 class="modal-title fs-5" id="exampleModalLabel">Formulario de Registro</h1>
                      <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                    </div>
                    <div class="modal-body">
                        <form [formGroup]="formSingup">
                            <div class="mb-3">
                              <label for="exampleInputEmail2" class="form-label" >Nickname</label>
                              <input type="email" class="form-control" id="exampleInputEmail2" aria-describedby="emailHelp" formControlName="nicknameRegister">
                            </div>
                            <div class="mb-3">
                              <label for="exampleInputPassword2" class="form-label" >Password</label>
                              <input type="password" class="form-control" id="exampleInputPassword2" formControlName="passwordRegister">
                            </div>
                            <div class="mb-3">
                                <label for="exampleInputEmailSingup2" class="form-label" >Correo electronico</label>
                                <input type="email" class="form-control" id="exampleInputEmailSingup2" formControlName="emailRegister">
                              </div>                
                            <button type="button" class="btn btn-dark" data-bs-toggle="modal" data-bs-target="#exampleModal" (click)="singup()">
                                Registrarse
                            </button>
                          </form>                
                    </div>
                  </div>
                </div>
              </div>
          </form>






      </div>
    </div>

    <div class="login-main__righth">
      <img
        class="img-bkg"
        src="../../../assets/piedraPapelTijera.jpg"
        alt=""
      />
    </div>
  </section>
