import { Component, OnInit } from '@angular/core';
import { GameService } from 'src/app/services/game.service.ts.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent implements OnInit {

  result: string;
  pointsUser = 0;
  pointsComp =  0;

  constructor(private playGame: GameService) {
    this.result = 'Esperando jugada...';
  }

  ngOnInit(): void {
    console.log(this.pointsUser);
  }

  /*
  s: tijeras
  p: papel
  r: piedra
  */

  play(choice: string): void {
    const result = this.playGame.play(choice);
    this.result = result;
    if (result === 'Ganaste!') {
      this.pointsUser++;
    } else if (result === 'Perdiste!') {
      this.pointsComp++;
    }
  }
}
<app-navbar-sidebars></app-navbar-sidebars>
<div class="title">
    <h1>¡¡Piedra, Papel o Tijera.!</h1>
</div>
<div class="score-board">
  <div id="user-label" class="badge">Usuarios</div>
  <div id="comp-label" class="badge">Maquina</div>
  <span id="user-score">{{ pointsUser }}</span>:<span id="comp-score">{{ pointsComp }}</span>
</div>
<p class="info-game">{{result}}</p>
  <div class="choices">
    <div class="choice" (click)="play('r')">
      <img src="assets/rock.png" />
    </div>
    <div class="choice" (click)="play('p')">
      <img src="assets/paper.png" />
    </div>
    <div class="choice" (click)="play('s')">
      <img src="assets/scissors.png" />
    </div>
  </div>
<p class="info-game">Selecciona tu jugada</p>
